query allReleases($fullPath: ID!, $first: Int, $last: Int, $before: String, $after: String, $sort: ReleaseSort) {
  project(fullPath: $fullPath) {
    releases(
      first: $first
      last: $last
      before: $before
      after: $after
      sort: $sort
    ) {
      nodes {
        ...Release
        __typename
      }
      pageInfo {
        startCursor
        hasPreviousPage
        hasNextPage
        endCursor
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment Release on Release {
  name
  tagName
  tagPath
  descriptionHtml
  releasedAt
  createdAt
  upcomingRelease
  assets {
    count
    sources {
      nodes {
        format
        url
        __typename
      }
      __typename
    }
    links {
      nodes {
        id
        name
        url
        directAssetUrl
        linkType
        external
        __typename
      }
      __typename
    }
    __typename
  }
  }
